(()=>{const a=document.querySelectorAll("[data-a2f]");if((a?.length||0)<2)return;let i=0;a.forEach(e=>{e.oninput=()=>{if(isNaN(e.value))e.value=0;if(e.value<0)e.value=0;if(e.value>9)e.value=9;if(i+1>a.length){let m=!0;for(let o=0;o<a.length;o++){if(!a[o].value){m=a[o];break}}
    if(m===!0){document.querySelector('[data-button-validate]')?.click()}else{m.focus()}}}
    i++})})()
function getNumbersCode(){const a=document.querySelectorAll("[data-a2f]");if((a?.length||0)<2)return"Too little input reported";let c='';a.forEach(e=>{c+=e.value});return c}
